select mas.refno_pfix Productcode, MAS.CCY Currency, mas.amount/POWER(10,CUR.C8CED) LCamount,  CASE WHEN (MAS.LIAB_AMT/POWER(10,CUR.C8CED)) IS NULL THEN (MAS.AMT_O_S/POWER(10,CUR.C8CED)) ELSE (MAS.LIAB_AMT/POWER(10,CUR.C8CED)) END OutstandingLiability, MAS.MASTER_REF from master mas, c8pf cur where mas.ccy= cur.c8ccy AND mas.master_ref = '{referenceNumber}' order by mas.master_ref